package StringsProblems;

import java.util.ArrayList;
import java.util.Arrays;

public class FindAllAnagramInString438
{
	//Leetcode 438 problem solution
	
	public static ArrayList<Integer> solution(String s, String p)
	{
		ArrayList<Integer> result = new ArrayList<>();
		char [] pch = p.toCharArray();
		Arrays.sort(pch);
		String pvalue = String.valueOf(pch);
		for(int i =0; i<= s.length()-p.length(); i++)
		{
			String sub = s.substring(i, i+p.length());
			char [] ch = sub.toCharArray();
			Arrays.sort(ch);
			String newString = String.valueOf(ch);
			if(newString.equals(pvalue))
				result.add(i);
				
		}
		return result;
	}
	public static void main(String [] args) throws InterruptedException
	{
		String s = "";
		String p = "yqrbgjdwtcaxzsnifvhmou";
		System.out.println(FindAllAnagramInString438.solution(s,p));
	}
}
